const handleSubmit = async (e) => {
  e.preventDefault();
  const validationErrors = validate();
  if (Object.keys(validationErrors).length > 0) {
    setErrors(validationErrors);
    return;
  }

  setErrors({});
  setSubmitted(true);

  try {
    const formToSave = {
      name: formData.name,
      phone: formData.phone,
      email: formData.email,
      preference1: formData.preference1,
      reason1: formData.reason1,
      preference2: formData.preference2,
      reason2: formData.reason2,
      preference3: formData.preference3,
      reason3: formData.reason3,
      submittedAt: new Date().toISOString(),
    };

    await push(ref(database, 'joinForms'), formToSave);
    console.log("✔️ Data sent to Realtime Database");
  } catch (err) {
    console.error("❌ Firebase submission error:", err);
  }
};
